(pubKey, privKey) = RSA.newkeys(512)

def generate_token(user_id):
    token_data = {"user_id": user_id, "expiration": datetime.now() + timedelta(hours=1)}
    token = jsonify({"token": RSA.encrypt(json.dumps(token_data), pubKey).encode('ascii')})
    return token

@app.route('/login', methods=['POST'])
