@app.route('/access', methods=['GET'])
def access():
    if not request.is_authenticated:
        return jsonify({"error": "Unauthorized access"}), 401

    try:
        token_data = RSA.decrypt(request.headers.get('Authorization'), privKey).decode('ascii')
    except (TypeError, ValueError):
        return jsonify({"error": "Invalid token format"}), 400

    if datetime.now() > token_data["expiration"]:
        return jsonify({"error": "Token expired"}), 401

    user_id = token_data["user_id"]

    # Check the user's access rights to the facility areas
    if not check_access(user_id, request.args.get('area')):
